import styled from "styled-components"

const page = styled.div`
  display: flex;
  align-items: stretch;
  height: 100vh;
`
const home = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  background: #835afd;
  color: #FFF;
  padding: 8rem 4rem;
  margin: -0.5rem;

  strong {
      font: 700 2rem 'Poppins', sans-serif;
      line-height: 2rem;
      margin-top: 1;
    }

  p {
    font: 400 36px 'Poppins', sans-serif;
    font-size: 1.2rem;
    line-height: 2rem;
    margin-top: 0.5rem;
    color: #f8f8f8;
  }
`

const login = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0.5rem;
  padding: 0 1rem;

  .main-content {
    display: flex;
    flex-direction: column;
    width: 100%;
    max-width: 16rem;
    align-items: stretch;
    text-align: center;
  }

  .log-code {
    height: 50px;
    border-radius: 8px;
    padding: 0 16px;
    background: #FFF;
    border: 1px solid #a8a8b3;
  }

  .log-button {
    margin-top: 16px;
  }
`

export default {
  page,
  home,
  login
}
