import styled from "styled-components"

const page = styled.div`
  display: flex;
  align-items: stretch;
  height: 100vh;
`
const home = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: #835afd;
    color: #FFF;
  `

const login = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`

export default {
  page,
  home,
  login
}
