import styled from "styled-components"

const page = styled.div`
  display: flex;
  align-items: stretch;
  height: 100vh;

`
const login = styled.div`
  width: 40rem;
  aside {
    background: #835afd;
    color: #FFF;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 0.5rem 1rem;

    .logo {
      max-width: 24rem;
    }

    strong {
      font: 700 2rem 'Roboto', sans-serif;
      line-height: 1.5rem;
      margin-top: 1rem;
    }

    p {
      font: 400 1.6rem 'Roboto', sans-serif;
      font-size: 0.8rem;
      line-height: 1.5rem;
      margin-top: 1rem;
      color: #f8f8f8;
    }
  }
`

const home = styled.div`

  background: #835afd;
  color: #FFF;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 0.5rem 1rem;

  img {
    max-width: 24rem;
  }

  strong {
    font: 700 2rem 'Roboto', sans-serif;
    line-height: 1.5rem;
    margin-top: 1rem;
  }

  p {
    font: 400 1.6rem 'Roboto', sans-serif;
    font-size: 0.8rem;
    line-height: 1.5rem;
    margin-top: 1rem;
    color: #f8f8f8;
  }
`

export default {
  page,
  home,
  login
}
