import styled from "styled-components"

const page = styled.div`
  .home {
    display: flex;
    align-items: stretch;
    height: 100vh;

    aside {
      flex: 7;
      background: #835afd;
      color: #FFF;
      display: flex;
      flex-direction: column;
      justify-content: center;
      /* padding: 0.5rem 1rem; */

      main {
        flex: 8;
      }

      img {
        max-width: 24rem;
      }

      strong {
        font: 700 1rem 'Roboto', sans-serif;
        line-height: 0.6rem;
        margin-top: 0.2rem;
      }

      p {
        font-size: 0.8rem;
        line-height: 0.6rem;
        margin-top: 0.2rem;
        color: #f8f8f8;
      }
    }
  }
`

export default {
  page
}
