import styled from "styled-components"

const page = styled.div`
  display: flex;
  align-items: stretch;
  height: 100vh;

  aside {
    flex: 7;
    background: #835afd;
    color: #FFF;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 0.5rem 1rem;

    main {
      flex: 8;
    }

    img {
      max-width: 100%;
    }
  }

`

export default {
  page
}
