import styled from "styled-components"

const page = styled.div`
  display: flex;
  align-items: stretch;
  height: 100vh;
`
const home = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  background: #835afd;
  color: #FFF;
  padding: 8rem 4rem;
  margin: -0.5rem;

  strong {
      font: 700 36px 'Poppins', sans-serif;
      line-height: 42px;
      margin-top: 16px;
    }

  p {
    font: 400 36px 'Poppins', sans-serif;
    font-size: 1.2rem;
    line-height: 2rem;
    margin-top: 1rem;
    color: #f8f8f8;
  }
`

const login = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`

export default {
  page,
  home,
  login
}
